let dt='<div class="col-lg-4 col-md-6 content-item" data-aos="fade-up">';fetch("https://newsapi.org/v2/top-headlines?q=corona&sources=bbc-news&apiKey=5b67480ba15d44889bbfcbf108d596ef").then(e=>e.json()).then(e=>{articles=e.articles,articles&&articles.length&&articles.forEach(e=>{document.getElementById("news-row").innerHTML+=dt+'<span><img src="'+e.urlToImage+'"width="80%"><h4>'+e.title+"</h4><p>"+e.description+'<a href="'+e.url+'">Read More</a></p></div>'})}).catch(e=>{});